        setComments(commentsFromApi);
    });
}, []);

console.log(comments)

return (
    <section>
    <h2>Comments</h2>
    <ol className="comments_list">
    {comments.map((comment) => {
        return (
            <li key={comment.comment_id}>

            </li>
        )
