import { useState, useEffect } from "react";
import { getReview } from "../utils/api";

const Review = () => {
const [isLoading, setIsLoading] = useState(true);
const [review, setReview] = useState(1);

useEffect(() => {
    setIsLoading(true);
    getReview(review).then(({ review }) => {
        setReviews(review);
        setIsLoading(false);
    });
}, []);





};

export default Review;
